import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { Form, Button, Row, Col } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import Fromcontainer from '../components/Fromcontainer'
import { register } from '../actions/userActions'

const RegisterScreen = ({ location, history }) => {
  const [name, setName] = useState('')
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [confirmPassword, setconfirmPassword] = useState('')
  const [message, setMessage] = useState(null)

  const dispatch = useDispatch()

  const userRegister = useSelector((state) => state.userRegister)
  const { loading, error, userInfo } = userRegister

  const redirect = location.search ? location.search.split('=')[1] : '/'

  useEffect(() => {
    if (userInfo) {
      history.push(redirect)
    }
  }, [history, userInfo, redirect])

  const submitHandler = (e) => {
    e.preventDefault()
    if (password !== confirmPassword) {
      setMessage('Passwords do not Macth!')
    } else {
      dispatch(register(name, email, password))
    }
  }

  return (
    <Fromcontainer>
      <h1 className='my-5' style={{ fontFamily: 'cursive', color: '#78c2ad' }}>
        Sign Up
      </h1>
      {message && <Message variant='danger'>{message}</Message>}
      {error && <Message variant='danger'>{error}</Message>}
      {loading && <Loader />}
      <Form onSubmit={submitHandler}>
        <Form.Group controlId='name'>
          <Form.Label style={{ fontFamily: 'cursive', fontSize: '20px' }}>
            Name
          </Form.Label>
          <Form.Control
            type='name'
            placeholder='Enter Name'
            value={name}
            onChange={(e) => setName(e.target.value)}
            style={{ fontFamily: 'cursive', fontSize: '20px' }}
          ></Form.Control>
        </Form.Group>

        <Form.Group controlId='email'>
          <Form.Label style={{ fontFamily: 'cursive', fontSize: '20px' }}>
            Email Address
          </Form.Label>
          <Form.Control
            type='email'
            placeholder='Enter Email'
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            style={{ fontFamily: 'cursive', fontSize: '20px' }}
          ></Form.Control>
        </Form.Group>

        <Form.Group controlId='password'>
          <Form.Label style={{ fontFamily: 'cursive', fontSize: '20px' }}>
            Password
          </Form.Label>
          <Form.Control
            type='password'
            placeholder='Enter Password'
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={{ fontFamily: 'cursive', fontSize: '20px' }}
          ></Form.Control>
        </Form.Group>

        <Form.Group controlId='confirmPassword'>
          <Form.Label style={{ fontFamily: 'cursive', fontSize: '20px' }}>
            Confirm Password
          </Form.Label>
          <Form.Control
            type='password'
            placeholder='Repeat Password'
            value={confirmPassword}
            onChange={(e) => setconfirmPassword(e.target.value)}
            style={{ fontFamily: 'cursive', fontSize: '20px' }}
          ></Form.Control>
        </Form.Group>

        <Button
          type='submit'
          variant='primary'
          style={{ fontFamily: 'cursive', fontSize: '20px' }}
        >
          Register
        </Button>
      </Form>

      <Row className='py-3' style={{ fontFamily: 'cursive', fontSize: '20px' }}>
        <Col>
          Have an Account ?
          <Link to={redirect ? `/login?redirect=${redirect}` : '/login'}>
            Login
          </Link>
        </Col>
      </Row>
    </Fromcontainer>
  )
}

export default RegisterScreen
